// MyISAInstPrinter.h
#ifndef LLVM_LIB_TARGET_MYISA_MYISAINSTPRINTER_H
#define LLVM_LIB_TARGET_MYISA_MYISAINSTPRINTER_H

#include "llvm/MC/MCInstPrinter.h"

namespace llvm {

class MCInstrInfo;
class MCRegisterInfo;

class MyISAInstPrinter : public MCInstPrinter {
public:
  MyISAInstPrinter(const MCAsmInfo &MAI, const MCInstrInfo &MII,
                   const MCRegisterInfo &MRI)
      : MCInstPrinter(MAI, MII, MRI) {}

  void printInst(const MCInst *MI, uint64_t Address, StringRef Annot,
                const MCSubtargetInfo &STI, raw_ostream &O) override;

  // Autogenerated by tblgen
  void printInstruction(const MCInst *MI, uint64_t Address, raw_ostream &O);
  static const char *getRegisterName(unsigned RegNo);

private:
  void printOperand(const MCInst *MI, unsigned OpNo, raw_ostream &O);
  void printImmediate(const MCInst *MI, unsigned OpNo, raw_ostream &O);
  void printRegister(const MCInst *MI, unsigned OpNo, raw_ostream &O);
};

} // namespace llvm

#endif // LLVM_LIB_TARGET_MYISA_MYISAINSTPRINTER_H